import React, { Component } from 'react';
import './App.css';

class Header extends Component {
  render() {
    return (
      <div className="Header">
          <p id="welcomeText">Welcome to the Food App</p>
      </div>
    );
  }
}

class Ingredient extends Component{
  constructor(props){
    super(props);
    this.state = {
      ingredientName: ' Arjun'
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event){
    this.setState({ingredientName: event.target.value});

  }
  render() {
    return (
      <input type="text" id="box" value={this.state.ingredientName} onChange={this.handleChange}/>
    )
  }
}


class UserInput extends Component {
  render() {
    return (
        <div className="formList">
          <p id="ingredientTitle">Enter Your Ingredients Below:</p>
          <ul id="ingredientList">
            <Ingredient />
          </ul>
        </div>
    );
  }

}

class Buttons extends Component {
  render() {
    return (
      <div className="Buttons">
         <button type="reset" id="ResetButton">Reset</button>
        <button type="button" id="SubmitButton">Submit</button>
      </div>
    )
  }
}

class Output extends Component {
  render() {
    return (
      <div id="OutputContainer">
        <p>Avaliable Recipes</p>
      </div>

    )
  }
}

class App extends Component {
  render() {
    return (
      <div className="AppContainer">
        <div id="Top">
          <Header />
        </div>

        <div id="MiddleLeft">
          <UserInput />
        </div>

        <div id="MiddleRight">
          <Output />
        </div>


        <div id="Bottom">
          <Buttons />
        </div>

      </div>
    );
  }
}

export default App;
