import React, { Component } from 'react';
import './App.css';

class Header extends Component {
  render() {
    return (
      <div className="Header">
          <p id="welcomeText">Welcome to the Food App</p>
      </div>
    );
  }
}

class Ingredient extends Component{
  constructor(props){
    super(props);
    this.state = {
      ingredientName: ''
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event){
    this.setState({ingredientName: event.target.value});

  }
  render() {
    return (
      <input type="text" id="box" value={this.state.ingredientName} onChange={this.handleChange}/>
    )
  }
}


class UserInput extends Component {
  constructor(props){
    super(props);
    this.in0 = React.createRef();
    this.in1 = React.createRef();
    this.in2 = React.createRef();
    this.in3 = React.createRef();
    this.in4 = React.createRef();
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(event){
    var ingredientList = [this.in0.current, this.in1.current, this.in2.current, this.in3.current, this.in4.current];
    for(var i = 0; i < 5; i++){
      alert(ingredientList[i]);
    }

  }

  render() {
    return (
        <div className="formList">
          <p id="ingredientTitle">Enter Your Ingredients Below:</p>
          <form id="ingredientForm">
            <Ingredient ref={this.in0}/><br></br>
            <Ingredient ref={this.in1}/><br></br>
            <Ingredient ref={this.in2}/><br></br>
            <Ingredient ref={this.in3}/><br></br>
            <Ingredient ref={this.in4}/><br></br>
            <div className="Buttons">
              <button type="button" id="AddButton" >Add Ingredient</button>
              <button type="button" id="SubmitButton" onClick={this.handleClick}>Submit</button>
            </div>

          </form>
        </div>
    );
  }

}

class Output extends Component {
  render() {
    return (
      <div id="OutputContainer">
        <p>Avaliable Recipes</p>
      </div>

    )
  }
}

class App extends Component {
  render() {
    return (
      <div className="AppContainer">
        <div id="Top">
          <Header />
        </div>

        <div id="MiddleLeft">
          <UserInput />
        </div>

        <div id="MiddleRight">
          <Output />
        </div>

      </div>
    );
  }
}

export default App;
