import React, { Component } from 'react';
import './App.css';


class Header extends Component {
  render() {
    return (
      <div className="Header">
          <p id="welcomeText">Welcome to the Food App</p>
      </div>
    );
  }
}

class User extends Component{
  constructor(props){
    super(props);
    this.state = {
      error: '',
      isLoaded: '',
      ingredientName: '',
      recipes: ''
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }

  handleChange(event){
    this.setState({ingredientName: event.target.value});

  }

  handleClick(event){
    alert(this.state.ingredientName);
  }

  ComponentDidMount(){
    fetch("https://www.food2fork.com/api/search?key=0b23e639e2a708b56e4e8cb0f575dfbf&q=chicken&page=1")
    .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            recipes: result.count
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error: "Error"
          });
        }
      )
  }

  render() {
    const {recipes, isLoaded, error} = this.state;
    return (
      <div>
        <p id="ingredientTitle">Enter Your Ingredients Below:</p>
        <input type="text" id="box" value={this.state.userName} onChange={this.handleChange}/>
        <div className="formList">
            <form id="ingredientForm">
              <div className="Buttons">
                <button type="button" id="AddButton" >Add Ingredient</button>
                <button type="button" id="SubmitButton" onClick={this.handleClick}>Submit</button>
              </div>
            </form>
            <h1>{recipes}</h1>
            <h1>{error}</h1>
          </div>
      </div>
    )
  }
}

class Output extends Component {
  render() {
    return (
      <div id="OutputContainer">
        <p>Avaliable Recipes</p>
      </div>

    )
  }
}

class App extends Component {
  render() {
    return (
      <div className="AppContainer">
        <div id="Top">
          <Header />
        </div>

        <div id="MiddleLeft">
          <User />
        </div>

        <div id="MiddleRight">
          <Output />
        </div>

      </div>
    );
  }
}

export default App;
