import React, { Component } from 'react';
import './App.css';


class Header extends Component {
  render() {
    return (
      <div className="Header">
          <p id="welcomeText">Welcome to the Food App</p>
      </div>
    );
  }
}

class List extends Component {
  constructor(props){
    super(props);
  }
  render() {
    return (
      <div className="Header">
          <p id="welcomeText">Welcome to the Food App</p>
      </div>
    );
  }
}

class User extends Component{
  constructor(props){
    super(props);
    this.state = {
      error: '',
      isLoaded: '',
      ingredientName: '',
      count: '',
      recipes: ''
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }

  handleChange(event){
    this.setState({ingredientName: event.target.value});

  }

  handleClick(event){
    alert(this.state.ingredientName);
  }

  componentDidMount(){
    fetch("https://www.food2fork.com/api/search?key=0b23e639e2a708b56e4e8cb0f575dfbf&q=cheese&page=1")
    .then(res => res.json())
    .then(
      (result) => {
        this.setState({
          isLoaded: true,
          count: result.count,
          recipes: result.recipes
        });
      }
    )
  }



  render() {
    console.log(this.state.recipes);
    const data  = this.state.recipes;
    data.map()

    return (
      <div>
        <p id="ingredientTitle">Enter Your Ingredients Below:</p>
        <input type="text" id="box" value={this.state.ingredientName} onChange={this.handleChange}/>
        <div className="formList">
            <form id="ingredientForm">
              <div className="Buttons">
                <button type="button" id="AddButton" >Add Ingredient</button>
                <button type="button" id="SubmitButton" onClick={this.handleClick}>Subit</button>
              </div>
            </form>
            <h1>{this.state.count}</h1>
            <h1>{data[0]}</h1>
          </div>
      </div>
    )
  }
}

class Output extends Component {
  render() {
    return (
      <div id="OutputContainer">
        <p>Avaliable Recipes</p>
      </div>

    )
  }
}

class App extends Component {
  render() {
    return (
      <div className="AppContainer">
        <div id="Top">
          <Header />
        </div>

        <div id="MiddleLeft">
          <User />
        </div>

        <div id="MiddleRight">
          <Output />
        </div>

      </div>
    );
  }
}

export default App;
